import React from 'react';

function CartView({ cart, onRemove, onCheckout }) {
  const total = cart.reduce((sum, item) => sum + (item.price * item.qty), 0);

  return (
    <div className="p-4">
      <h1 className="text-2xl mb-4">Shopping Cart</h1>
      {cart.length === 0 ? (
        <p>Cart is empty</p>
      ) : (
        <>
          <ul className="mb-4">
            {cart.map(item => (
              <li key={item.id} className="flex justify-between mb-2">
                <span>{item.name || `Item ${item.productId}`} (x{item.qty}) - ${item.price}</span>
                <button onClick={() => onRemove(item.id)} className="text-red-600">Remove</button>
              </li>
            ))}
          </ul>
          <p className="text-xl font-bold">Total: ${total}</p>
          <button onClick={() => {
  fetch('http://localhost:5000/api/cart', { method: 'DELETE' })
    .then(() => onRemove(0)); // 0 = dummy to refresh cart
}} className="mt-4 mr-4 bg-red-600 text-white px-4 py-2 rounded">
  Clear Cart
</button>
          <button onClick={onCheckout} className="mt-4 bg-blue-600 text-white px-4 py-2">Checkout</button>
        </>
      )}
    </div>
  );
}

export default CartView;
