import React, { useState } from 'react';
import ProductGrid from './components/ProductGrid';
import CartView from './components/CartView';
import CheckoutForm from './components/CheckoutForm';

function App() {
  const [view, setView] = useState('products'); // 'products', 'cart', 'checkout'
  const [cart, setCart] = useState([]);
  const [receipt, setReceipt] = useState(null);

  const handleAddToCart = (productId, qty = 1) => {
    // POST to backend
    fetch('http://localhost:5000/api/cart', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ productId, qty })
    }).then(() => fetchCart());
  };

  const fetchCart = () => {
    fetch('http://localhost:5000/api/cart')
      .then(res => res.json())
      .then(data => setCart(data.items || []));
  };

  const handleRemoveFromCart = (id) => {
    fetch(`http://localhost:5000/api/cart/${id}`, { method: 'DELETE' })
      .then(() => fetchCart());
  };

 const handleCheckout = (formData) => {
  fetch('http://localhost:5000/api/checkout', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ cartItems: cart,  name: formData.name, email: formData.email })
  })
    .then(res => res.json())
    .then(data => {
      setReceipt(data.receipt);   // ← THIS WAS MISSING!
      setCart([]);                // clear cart
      setView('products');        // go home
    })
    .catch(err => alert("Checkout failed! Is backend running?"));
};

  return (
    <div className="min-h-screen bg-gray-100">
      <nav className="bg-blue-600 p-4 text-white">
        <button onClick={() => setView('products')} className="mr-4">Products</button>
        <button onClick={() => { setView('cart'); fetchCart(); }} className="mr-4">Cart ({cart.length})</button>
        {view === 'checkout' && <button onClick={() => setView('cart')}>Back to Cart</button>}
      </nav>
      {view === 'products' && <ProductGrid onAddToCart={handleAddToCart} />}
      {view === 'cart' && <CartView cart={cart} onRemove={handleRemoveFromCart} onCheckout={() => setView('checkout')} />}
      {view === 'checkout' && <CheckoutForm onSubmit={handleCheckout} />}
      {receipt && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded max-w-md w-full mx-4">
            <h2 className="text-xl mb-4">Receipt</h2>
            <p>Total: ${receipt.total}</p>
            <p>Time: {receipt.timestamp}</p>
            <p>Thank you, {receipt.customer.name}!</p>
            <button onClick={() => { setReceipt(null); setView('products'); }} className="mt-4 bg-blue-600 text-white px-4 py-2">Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
